mapper.update:
UPDATE board_anonymous
SET viewCount=viewCount+1
WHERE num=#{num}

mapper.select:
SELECT a.* FROM(SELECT num, (SELECT br2.num FROM board_anonymous br2 WHERE br2.num < board_anonymous.num ORDER BY br2.num DESC LIMIT 1) prevNum, (SELECT MIN(br2.num)FROM board_anonymous br2 WHERE br2.num > board_anonymous.num ORDER BY br2.num DESC) nextNum, writer, title, content, viewCount, regdate FROM board_anonymous
OR writer LIKE CONCAT('%',#{writer},'%')
OR title LIKE CONCAT('%',#{title},'%')
OR content LIKE CONCAT('%',#{content},'%')
) AS a WHERE num=#{num}

mapper.update:
UPDATE board_anonymous
SET title=#{title}, content=#{content}
WHERE num=#{num}
